mapper.select:
select node.*
from node
where node.node_id in (
    select next.next_id
    from next
    where next.node_id = #{nodeId}
)
  and node.is_private = #{isPrivate}
limit #{pageNumber}, #{pageSize}

select count(*)
from node
where node.node_id in (
    select next.next_id
    from next
    where next.node_id = #{nodeId}
)
  and node.is_private = #{isPrivate}

select node.*
from node
where node.node_id in (
    select next.next_id
    from next
    where next.node_id = #{nodeId}
)
  and node.is_private = #{isPrivate}
  and node.node_name like #{title}
    limit #{pageNumber}, #{pageSize}

select count(*)
from node
where node.node_id in (
    select next.next_id
    from next
    where next.node_id = #{nodeId}
)
  and node.is_private = #{isPrivate}
  and node.node_name like #{title}
