mapper.select:
select node.*
from node
where node.node_id in (
    select previous.previous_id
    from previous
    where previous.node_id = #{nodeId}
    )
and node.is_private = 0
limit #{pageNumber}, #{pageSize}

select node.*
from node
where node.node_id in (
    select previous.previous_id
    from previous
    where previous.node_id = #{nodeId}
)
  and node.is_private = 0
  and node.node_name like #{title}
    limit #{pageNumber}, #{pageSize}
